ass=&#34;number&#34;&gt;3&lt;/span&gt;])               &lt;span class=&#34;comment&#34;&gt;# 切片行&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;built_in&#34;&gt;print&lt;/span&gt;(df.loc[:, &lt;span class=&#34;string&#34;&gt;&amp;#x27;Name&amp;#x27;&lt;/span&gt;])     &lt;span class=&#34;comment&#34;&gt;# 提取单列&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;built_in&#34;&gt;print&lt;/span&gt;(df.loc[&lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;:&lt;span class=&#34;number&#34;&gt;2&lt;/span&gt;, [&lt;span class=&#34;string&#34;&gt;&amp;#x27;Name&amp;#x27;&lt;/span&gt;, &lt;span class=&#34;string&#34;&gt;&amp;#x27;Age&amp;#x27;&lt;/span&gt;]])  &lt;span class=&#34;comment&#34;&gt;# 标签索引提取指定行列&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;built_in&#34;&gt;print&lt;/span&gt;(df.iloc[:, &lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;:])        &lt;span class=&#34;comment&#34;&gt;# 位置索引提取指定列&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;
